import {Avatar, IconButton} from '@material-ui/core';
import React from 'react';
import {useEffect,useState} from 'react'
import './Chat.css'
import { useParams } from 'react-router-dom';
import {AttachFile, MoreVert, SearchOutlined} from '@material-ui/icons';
import MicIcon from '@material-ui/icons/Mic';
import InsertEmoticonIcon from '@material-ui/icons/InsertEmoticon';
import db from './firebase';
function Chat() {
    const [input, setInput] = useState("");
    const [seed, setSeed] = useState("");
    const {roomId}= useParams("abc");
    const [roomName, setRoomName] = useState("");
    useEffect(() => {
        setSeed(Math.random() * 5000);
    }, [])
    useEffect(()=>{
        
        if(roomId){

            db.collection("rooms").doc("abc").onSnapshot(snapshot => {
                setRoomName(snapshot.data().name);
            });

            // db.collection('rooms').doc(roomId).collection("messages").orderBy("timestamp","asc").onSnapshot(snapshot => {
            //     setMessages(snapshot.docs.map(doc => doc.data()))
            // });

        }else{
            console.log("no rrom")
            db.collection("rooms").doc("abc").onSnapshot(snapshot => {
                setRoomName(snapshot.data().name);
            });

        }
    },[roomId])
    const sendMessage = (e) => {
        e.preventDefault();
        console.log("Dattebayo...naruto kun....",input);
        setInput("");
    };

    return (
        <div className="chat">
            <div className="chat__header">
                <Avatar scr={`https://avatars.dicebear.com/api/human/${seed}.svg`}/> 
                <div className="chat__headerInfo">
                   <h3>{roomName}</h3>
                   <p>Last Seen ...</p>
                    </div>      
                <div className="chat__headerRight">
                <IconButton>
                        <SearchOutlined/>
                    </IconButton>
                    <IconButton>
                        <AttachFile/>
                    </IconButton>
                    <IconButton>
                        <MoreVert/>
                    </IconButton>
                    </div>                  
            </div>
            <div className="chat__body">
                <p className={`chat__message ${false && "chat__receiver"}`}>
                    <span className="chat__name">khyatin kadam</span>
                never give up ne
                <span className="chat__timestamp">09:30am</span>
                </p>
                <p className={`chat__message ${true && "chat__receiver"}`}>
                    <span className="chat__name">khyatin kadam</span>
                never give up ne
                <span className="chat__timestamp">09:30am</span>
                </p>
            </div>
            <div className="chat__footer">
            <InsertEmoticonIcon />
                <form>
                    <input value={input} onChange={(e)=>setInput(e.target.value)} type="text" placeholder="Type a message"/>
                    <button type="submit" onClick={sendMessage}> Send a Message</button>
                </form>
                <MicIcon/>
            </div>
        </div>
    )
}

export default Chat
